from scrapy import Spiderfrom scrapy.linkextractors import LinkExtractorfrom scrapy.spiders import Rulefrom urllib.parse import urljoinclass PycoderSpider(Spider):    name = "pycoder"    start_urls = [        'http://pycoder.ru',    ]    def parse(self, response):        for post_link in response.xpath(                '//div[@class="post mb-2"]/h2/a/@href').extract():            url = urljoin(response.url, post_link)            print(url)